mapper.select:
SELECT OS_SYS_MENU.MENU_ID, OS_SYS_MENU.MENU_NAME, OS_SYS_MENU.PARENT_ID, OS_SYS_MENU.PATH, OS_SYS_MENU.COMPONENT, OS_SYS_MENU.IS_CACHE, OS_SYS_MENU.VISIBLE, OS_SYS_MENU.IS_FRAME, OS_SYS_MENU.MENU_TYPE, OS_SYS_MENU.ORDER_NUM, OS_SYS_MENU.MENU_STATUS, OS_SYS_MENU.PERMS, OS_SYS_MENU.AFFIX, OS_SYS_MENU.BREADCRUMB, OS_SYS_MENU.AlWAYSSHOW, OS_SYS_MENU.REMARK, OS_SYS_MENU.UPDATE_TIME, OS_SYS_MENU.CREATE_TIME, OS_SYS_MENU.ICON, OS_SYS_MENU.OS_ID, OS_SYS_MENU.VERSION, OS_SYS_MENU.DELETED FROM OS_SYS_MENU WHERE MENU_STATUS = 1 AND MENU_TYPE IN ('C', 'M') AND DELETED = 1
AND MENU_NAME like CONCAT('%',#{menu.menuName},'%')
AND OS_ID = #{menu.osId}
AND MENU_STATUS = #{menu.status}
ORDER BY OS_SYS_MENU.PARENT_ID ASC, OS_SYS_MENU.ORDER_NUM ASC
